import React, { Component } from "react";
import Searchbox from "./Searchbox.js";
import { robots } from "./robots";
import Cardlist from "./cardlist";


class App extends Component{
    constructor(){
      super()
      this.state={
       robos: robots,
       searchfield:''
      }
    }
     onsearchange=(event)=>{
        const Searchrobots = this.state.robos.filter((robos)=>{
           return robos.name.toLocaleLowerCase().includes(this.state.searchfield.toLocaleLowerCase());
        })
        console.log(Searchrobots);
     }
    //  When u make own methods in a component, Arrow func it.
      render(){
        return(
          <>
          <h1>RoboFriends</h1>
          <Searchbox searchchange={this.onsearchange}/>
          {/* this. coz Onsearchange is an object */}
          <Cardlist robo={this.state.robos}/>
          {/* From here robo is passed to Cardlist {robo} */}
          {/* this.state. coz robots is passed as props via state */}
          </>
        )
      }
}



export default App;
